import React, {useState} from 'react';
import star from '../assets/icon-star.svg';
import './rate.css';

const [rate, setRate] = useState(null);
const [submitted, setSubmitted] = useState(false);

const content = {
  title: "How did we do?",
  description: "Please let us know how we did with your support request. All feedback is appreciated to help us improve our offering!",
  maxRating: 5
}

const RateComponent = ({ num, handler, rate }) => {
  return (
      <button
          type="button"
          className={`card__rate-btn${rate === num ? " active" : ""}`}
          onClick={() => handler(num)}
      >
          {num}
      </button>
  )
}

const Rate = (
  { maxRating = content.maxRating }

) => {
  const handleRate = num => {
    if (rate !== num)
        setRate(num);
  }

  const handleSubmit = () => { setSubmitted(true) };

  const rateArr = [];
  for (let i = 0; i < maxRate; i++) {
    rateArr.push(
        <RateComponent
            num={i + 1}
            key={i}
            handler={handleRate}
            rate={rate}
        />
    )
  }

  return (
    <div className="card__container">
    <div className={`card__content${submitted ? " back" : ""}`}>
        <div className="card__rating">
            <div className="card__icon-wrapper">
                <img src={images.icon} alt="" aria-hidden="true" className="card__icon" />
            </div>
            <div className="card__message">
                <h2 className="card__title">{title}</h2>
                <p className="card__description">{description}</p>
                <div className="card__rating-wrapper">{rateArr}</div>
            </div>
            <button
                type="submit"
                className="card__cta"
                onClick={handleSubmit}
                disabled={!rate}
            >
                submit
            </button>
        </div>
        <div className={`card__thanks${submitted ? " active" : ""}`}>
            <div className="card__illustration-wrapper">
                <img src={images.illustration} alt="mobile phone illustration" className="card__ilustration" />
            </div>
            <div className="card__rate-result-wrapper">
                <div className="card__rate-result">
                    You selected {rate} out of {content.maxRating}
                </div>
            </div>
            <div className="card__thanks-message">
                <h2>Thank you!</h2>
                <p>We appreciate you taking the time to give a rating. If you ever need more support, don't hesitate to get in touch!</p>
            </div>
        </div>
    </div>
</div>
  );
};

export default Rate;
